const FRreadingProgress=e=>{let r=window.pageYOffset||document.documentElement.scrollTop,s=e.offsetTop+e.scrollHeight,t=r+window.outerHeight,o=100-(s-t+window.outerHeight/3)/(s-window.outerHeight+window.outerHeight/3)*100;document.querySelector("#gs-progress-bar").style.width=o+"%",o>100?document.querySelector("#gs-progress-container").classList.add("ready"):document.querySelector("#gs-progress-container").classList.remove("ready")};document.addEventListener("DOMContentLoaded",()=>{let e=document.querySelector(".site-content"),r=document.createElement("div");r.id="gs-progress-container",r.insertAdjacentHTML("beforeend",'<div id="gs-progress-bar"><style scoped>#gs-progress-container{position:fixed;width:100%;height:4px;left:0;top:0;z-index:1000000;background:0 0;}#gs-progress-container.ready{transform:translateY(-3px)}#gs-progress-bar{display:block;width:0;height:3px;background:var(--wp--preset--color--primary)}</style></div>'),document.body.appendChild(r),FRreadingProgress(e),window.addEventListener("scroll",()=>{FRreadingProgress(e)})});